function SearchBar()
{
    // 검색창 부분
    return <form onSubmit={event=>{
        event.preventDefault();
        const search = event.target.search.value;
        props.onCreate(search);
    }}>
        <p><input type="text" name="search" placeholder="Search..." onkeypress="if(event.keycode ==13){Search();}"/></p>
    </form>
}


function Result()
{
    return (
        <div>
            {/* 실제로 화면에 보이는 부분 */}
            <SearchBar></SearchBar> 
            {/* 검색창 부분 */}
        </div>
    );
}

export default Main;